$(document).ready(function() {
    $('a.forumlink').click(
        function confirmRedirect(event) {
            if (confirm("This link will take you to another website. Click OK if you wish to continue.")) {
                return true; // Allow the redirect
            } else {
                event.preventDefault(); // Prevent the default behavior (don't redirect)
                return false;
            }
        }
    );
});